const url2 = 'https://api.themoviedb.org/3/discover/movie?include_adult=false&include_video=false&language=en-US&page=1&primary_release_date.gte=2023-10-01&sort_by=popularity.desc&watch_region=India&with_original_language=kn';
const url1 = 'https://api.themoviedb.org/3/discover/movie?include_adult=false&include_video=false&language=en-US&page=1&primary_release_date.gte=2023-10-01&sort_by=popularity.desc&watch_region=India&with_original_language=en';
useEffect(()=>{
  const options = {
