      Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI1YzY5NDY1M2YxMzZmZDYxMmIxNThiMDk1YTY2NzhiNCIsInN1YiI6IjY1M2I5NWZhNTkwN2RlMDEzOGUwYmU5OCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.MwaTcNsdK_qDhxuLIZLos7KvVhwga1wyHdmaUzJ_no0'
    }
  };
  var te=0;
var ta=0;
var hi=0;
const en_array=[];
const ta_array=[];
const kn_array=[];
const te_array=[];
const hi_array=[];
  fetch(url2, options)
  .then(res => res.json())
  .then(json => {
    const movies=json.results;
    kn_array[0]=movies[0];
    kn_array[1]=movies[1];
    state1(kn_array);
  })
  .catch(err => console.error('error:' + err));
  fetch(url, options)
    .then(res => res.json())
    .then(json => {
      const movies=json.results;
      movies.map(movie => {
          if(movie.original_language==="te"&&te<2)
          {
              te_array[te]=movie;
              te=te+1;
          }
          if(movie.original_language==="ta"&&ta<2)
          {
              ta_array[ta]=movie;
              ta=ta+1;
          }
          if(movie.original_language==="hi"&&hi<2)
          {
              hi_array[hi]=movie;
              hi=hi+1;
          }
      })
      state2(te_array);
      state3(ta_array);
      state4(hi_array);
    })
    .catch(err => console.error('error:' + err));
  
    fetch(url1, options)
    .then(res => res.json())
    .then(json => {
      const movies=json.results;
      en_array[0]=movies[0];
      en_array[1]=movies[1];
      state5(en_array);
    })
    .catch(err => console.error('error:' + err));
},[])

  
